// Analyse de la requ√™te utilisateur
const userQuery = $json.query;
const userId = $json.userId || 'anonymous';
const userRole = $json.userRole || 'employee';
const timestamp = new Date().toISOString();

// Classification de la requ√™te
let queryType = 'general';
let needsRealTimeData = false;
let requiresPrivilegedAccess = false;

// D√©tection du type de requ√™te
if (userQuery.toLowerCase().includes('stock') || userQuery.toLowerCase().includes('inventaire')) {
  queryType = 'inventory';
  needsRealTimeData = true;
}
if (userQuery.toLowerCase().includes('salaire') || userQuery.toLowerCase().includes('rh')) {
  queryType = 'hr';
  requiresPrivilegedAccess = true;
}
if (userQuery.toLowerCase().includes('vente') || userQuery.toLowerCase().includes('chiffre')) {
  queryType = 'sales';
  requiresPrivilegedAccess = true;
}
if (userQuery.toLowerCase().includes('politique') || userQuery.toLowerCase().includes('proc√©dure')) {
  queryType = 'policy';
}

// Formatage pour embedding
const queryForEmbedding = userQuery.replace(/[^a-zA-Z0-9 √†√°√¢√§√©√®√™√´√Ø√Æ√¥√∂√π√ª√º√ø√ß]/g, '').toLowerCase();

return {
  userQuery,
  userId,
  userRole,
  timestamp,
  queryType,
  needsRealTimeData,
  requiresPrivilegedAccess,
  queryForEmbedding,
  sessionId: `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
};

// Agent Orchestrateur - D√©termine quelle strat√©gie utiliser
const queryType = $('Analyseur de Requ√™te').item.json.queryType;
const userQuery = $('Analyseur de Requ√™te').item.json.userQuery;
const vectorResults = $('Recherche Vectorielle').item.json.result || [];
const realtimeData = $input.all().find(item => item.json.source === 'realtime') || null;

// Analyse de la pertinence des r√©sultats
let hasRelevantKnowledge = false;
let contextScore = 0;

if (vectorResults.length > 0) {
  // Calcul du score de pertinence
  const avgScore = vectorResults.reduce((sum, result) => sum + result.score, 0) / vectorResults.length;
  contextScore = avgScore;
  hasRelevantKnowledge = avgScore > 0.7; // Seuil de pertinence
}

// D√©cision de routage
let agentStrategy = 'hybrid'; // Par d√©faut
let requiresSpecializedAgent = false;
let needsMultiStep = false;

if (queryType === 'inventory' && realtimeData) {
  agentStrategy = 'realtime_focused';
  requiresSpecializedAgent = true;
}
else if (queryType === 'hr' || queryType === 'sales') {
  agentStrategy = 'policy_expert';
  requiresSpecializedAgent = true;
}
else if (!hasRelevantKnowledge && contextScore < 0.5) {
  agentStrategy = 'clarification_needed';
  needsMultiStep = true;
}
else if (userQuery.toLowerCase().includes('compare') || userQuery.toLowerCase().includes('analyse')) {
  agentStrategy = 'analytical';
  needsMultiStep = true;
}

// Pr√©paration du contexte pour les agents
const contextDocuments = vectorResults.map(result => ({
  content: result.payload.content,
  source: result.payload.source,
  score: result.score,
  metadata: result.payload.metadata
}));

const realtimeContext = realtimeData ? {
  data: realtimeData.json.data,
  source: realtimeData.json.source,
  timestamp: realtimeData.json.timestamp
} : null;

return {
  agentStrategy,
  requiresSpecializedAgent,
  needsMultiStep,
  hasRelevantKnowledge,
  contextScore,
  contextDocuments,
  realtimeContext,
  orchestratorDecision: {
    confidence: contextScore,
    reasoning: `Strat√©gie ${agentStrategy} choisie bas√©e sur type ${queryType}, pertinence ${contextScore.toFixed(2)}`,
    nextSteps: requiresSpecializedAgent ? ['route_to_specialist'] : ['direct_generation']
  }
};

