// Agr√©gation et synth√®se finale
const originalQuery = $('Analyseur de Requ√™te').item.json.userQuery;
const sessionId = $('Analyseur de Requ√™te').item.json.sessionId;
const agentStrategy = $('Agent Orchestrateur').item.json.agentStrategy;
const contextScore = $('Agent Orchestrateur').item.json.contextScore;

// Collecte des r√©ponses des diff√©rents agents
const responses = [];
const sources = [];
let confidence = 0;

// Traitement des r√©ponses selon l'agent activ√©
if ($input.all().find(item => item.json.choices)) {
  const agentResponse = $input.all().find(item => item.json.choices);
  const response = agentResponse.json.choices[0].message.content;

  responses.push({
    agent: agentStrategy,
    content: response,
    confidence: contextScore
  });

  // Extraction des sources cit√©es
  const sourceMatches = response.match(/\[([^\]]+)\]/g);
  if (sourceMatches) {
    sourceMatches.forEach(match => {
      const source = match.replace(/[\[\]]/g, '');
      if (!sources.includes(source)) {
        sources.push(source);
      }
    });
  }

  confidence = contextScore;
}

// Pr√©paration de la r√©ponse finale
let finalResponse = "";
let metadata = {};

if (responses.length > 0) {
  finalResponse = responses[0].content;

  // Ajout de m√©tadonn√©es sur la qualit√© de la r√©ponse
  let qualityIndicator = "";
  if (confidence > 0.8) {
    qualityIndicator = "üü¢ R√©ponse de haute confiance";
  } else if (confidence > 0.5) {
    qualityIndicator = "üü° R√©ponse de confiance mod√©r√©e";
  } else {
    qualityIndicator = "üî¥ R√©ponse de faible confiance - V√©rification recommand√©e";
  }

  metadata = {
    sessionId,
    strategy: agentStrategy,
    confidence: confidence.toFixed(2),
    qualityIndicator,
    sourcesUsed: sources.length,
    timestamp: new Date().toISOString(),
    processingTime: Date.now() - parseInt(sessionId.split('_')[1])
  };

} else {
  finalResponse = "Je n'ai pas pu traiter votre demande. Veuillez reformuler votre question ou contacter le support.";
  metadata = {
    sessionId,
    strategy: 'error',
    confidence: 0,
    qualityIndicator: "‚ùå Erreur de traitement",
    timestamp: new Date().toISOString()
  };
}

// Log pour audit
const auditLog = {
  sessionId,
  userQuery: originalQuery,
  strategy: agentStrategy,
  confidence,
  sourcesCount: sources.length,
  responseLength: finalResponse.length,
  timestamp: new Date().toISOString()
};

return {
  response: finalResponse,
  metadata,
  sources,
  auditLog
};

